import PlayerList from "./playerList.js";
import { useState } from "react";
import AddCard from "./addCard.js";
import DisplayMinutes  from "./minutesDisplay.js";
export default function NBARotation() {
   const [name, setName] = useState("");
   const [minutes, setMinutes] = useState(1);
   const [position, setPosition] = useState("Point Guard");
   const [players, setPlayers] = useState([]);
   const [warning, setWarning] = useState("");
   function handleFormSubmit(event) {
      event.preventDefault();
      if (!name) {
         setWarning("Please enter a name!");
         return;
      }
      setPlayers([...players, {
         id: players.length + 1,
         name: name, 
         position: position, minutes: minutes
      }]);
      setName("");
      setPosition("Point Guard");
   }
   return <>
   <AddCard name={name} position={position} onFormSubmit={handleFormSubmit} onNameChange={e => setName(e.target.value)} onPositionChange={e => setPosition(e.target.value)} warning={warning} minutes={minutes} onMinutesChange = {e => setMinutes(e.target.value)}/>
   <PlayerList players={players}/>
   </>;
  
}
