import PlayerList from "./playerList.js";
import { useState } from "react";
import AddCard from "./addCard.js";
import DisplayMinutes  from "./minutesDisplay.js";
import clsx from "clsx";
export default function NBARotation() {
   const [name, setName] = useState("");
   const [minutes, setMinutes] = useState(1);
   const [position, setPosition] = useState("Point Guard");
   const [players, setPlayers] = useState([]);
   const [warning, setWarning] = useState("");
   const [PG, setPG] = useState();
   const [SG, setSG] = useState();
   const [SF, setSF] = useState();
   const [PF, setPF] = useState();
   const [C, setC] = useState();
   function HandleMinutesChange(event) {
      setMinutes(event.target.value);
      
   }
   function handleFormSubmit(event) {
      event.preventDefault();
      if (!name) {
         setWarning("Please enter a name!");
         return;
      }
      setPlayers([...players, {
         id: players.length + 1,
         name: name, 
         position: position, minutes: minutes
      }]);
      position === "Point Guard" && setPG((prev) => prev + minutes);
      position === "Shooting Guard" && setSG((prev) => prev + minutes);
      position === "Small Forward" && setSF((prev) => prev + minutes);
      position === "Power Forward" && setPF((prev) => prev + minutes);
      position === "Center" && setC((prev) => prev + minutes);
      setName("");
      setMinutes(1);
   }
   console.log(PG)
   return <>
   <AddCard name={name} position={position} onFormSubmit={handleFormSubmit} onNameChange={e => setName(e.target.value)} onPositionChange={e => setPosition(e.target.value)} warning={warning} minutes={minutes} onMinutesChange = {HandleMinutesChange}/>
   <PlayerList players={players}/>
   <DisplayMinutes PG={PG}/>
   </>;
  
}
