import PlayerList from "./playerList.js";
import { useState } from "react";
import AddCard from "./addCard.js";

export default function NBARotation() {
   const [name, setName] = useState("");
   const [minutes, setMinutes] = useState(0);
   const [position, setPosition] = useState("Point Guard");
   const [players, setPlayers] = useState([]);
   const [warning, setWarning] = useState("");
   function handleFormSubmit(event) {
      event.preventDefault();
      if (!name) {
         setWarning("Please enter a name!");
         
      }
      setPlayers([...players, {
         id: players.length + 1,
         name: name, 
         position: position
      }]);
      setName("");
      setPosition("");
   }
   return <>
   <AddCard name={name} position={position} onFormSubmit={handleFormSubmit} onNameChange={e => setName(e.target.value)} onPositionChange={e => setPosition(e.target.value)}/>
   <PlayerList players={players}/>
   </>;
  
}
